import{H as c,F as d}from"./Footer-C6GYhFVc.js";import{_ as v,c as s,d as t,A as i,o as l}from"./index-DEEuHbY5.js";const e={class:"about-page"},f={__name:"about",setup(o){return(h,a)=>(l(),s("div",e,[t(c),a[0]||(a[0]=i('<main class="about-main" data-v-358fc4a7><div class="container" data-v-358fc4a7><header class="about-hero" data-v-358fc4a7><h1 class="page-title" data-v-358fc4a7>О компании «Аквилон»</h1><p class="page-subtitle" data-v-358fc4a7> Более 15 лет работаем с материалами для корпусной мебели в Саратове — поставляем и доводим детали до состояния “можно собирать”. </p></header><div class="about-content" data-v-358fc4a7><section class="section card" data-v-358fc4a7><div class="section-head" data-v-358fc4a7><h2 data-v-358fc4a7>Кто мы</h2><p class="lead" data-v-358fc4a7> «Аквилон» — поставщик древесно-плитных материалов и услуг по их обработке. Работаем с мебельными производствами и частными мастерами: помогаем быстро получить нужный материал и подготовить детали под сборку. </p></div><div class="grid-2" data-v-358fc4a7><div class="info-card" data-v-358fc4a7><h3 data-v-358fc4a7>Материалы</h3><ul class="list" data-v-358fc4a7><li data-v-358fc4a7> ЛДСП, ЛМДФ и ДВПО от заводов: «РосПлит», Череповецкий ФМК, «Монза» (Верхневохтожский ДОК), «ЕВРОПЛИТ» и SWISS KRONO. </li><li data-v-358fc4a7> Все материалы имеют необходимые сертификаты качества и санитарно-эпидемиологические заключения и соответствуют требованиям ГОСТ. </li></ul></div><div class="info-card" data-v-358fc4a7><h3 data-v-358fc4a7>Обработка</h3><ul class="list list-check" data-v-358fc4a7><li data-v-358fc4a7>Раскрой ЛДСП на современном оборудовании</li><li data-v-358fc4a7>Кромление постформингом и ПВХ</li><li data-v-358fc4a7>Фрезеровка под фурнитуру (в т.ч. под петли)</li><li data-v-358fc4a7>Изготовление гнутых радиусов и их кромление</li></ul></div></div></section><section class="section callout" data-v-358fc4a7><div class="callout-inner" data-v-358fc4a7><h2 data-v-358fc4a7>Доставка</h2><p data-v-358fc4a7> Организуем доставку в любой район города и ближайшие окрестности. Закрываем полный цикл — от подбора материала до обработки и логистики — чтобы вы не тратили время на лишнюю координацию. </p></div></section><section class="section card" data-v-358fc4a7><div class="section-head" data-v-358fc4a7><h2 data-v-358fc4a7>Наша история</h2><p class="muted" data-v-358fc4a7> Основа нашего долголетия — гибкий подход и ответственность за результат. </p></div><div class="prose" data-v-358fc4a7><p data-v-358fc4a7> Компания «Аквилон» более 15 лет работает на рынке материалов для корпусной мебели в Саратове. С самого начала мы сделали ставку на понятный сервис и предсказуемое качество — без “как-нибудь сделаем”. </p><p data-v-358fc4a7> Мы начинали с поставок качественных материалов и постепенно расширяли спектр услуг, ориентируясь на реальные потребности рынка. Сегодня «Аквилон» — это не просто склад, а партнёр, который берёт на себя этапы работы с материалом: от выбора сырья и раскроя до точной обработки и доставки. </p><p data-v-358fc4a7> Нас выбирают и крупные мебельные производства, и частные мастера: мы одинаково внимательно относимся к срокам, точности и качеству. Эти принципы остаются неизменными — и именно на них строится наша репутация в Саратове. </p></div></section><section class="values-section" data-v-358fc4a7><h2 data-v-358fc4a7>Наши ценности</h2><div class="values-grid" data-v-358fc4a7><div class="value-card" data-v-358fc4a7><div class="value-icon" data-v-358fc4a7><svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor" data-v-358fc4a7><path d="M12 1L3 5v6c0 5.55 3.84 10.74 9 12 5.16-1.26 9-6.45 9-12V5l-9-4zm0 10.99h7c-.53 4.12-3.28 7.79-7 8.94V12H5V6.3l7-3.11v8.8z" data-v-358fc4a7></path></svg></div><h3 data-v-358fc4a7>Качество</h3><p data-v-358fc4a7>Работаем с сертифицированными материалами и контролируем результат на каждом этапе</p></div><div class="value-card" data-v-358fc4a7><div class="value-icon" data-v-358fc4a7><svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor" data-v-358fc4a7><path d="M23 12l-2.44-2.78.34-3.68-3.61-.82-1.89-3.18L12 3 8.6 1.54 6.71 4.72l-3.61.81.34 3.68L1 12l2.44 2.78-.34 3.69 3.61.82 1.89 3.18L12 21l3.4 1.46 1.89-3.18 3.61-.82-.34-3.68L23 12zm-10 5h-2v-2h2v2zm0-4h-2V7h2v6z" data-v-358fc4a7></path></svg></div><h3 data-v-358fc4a7>Надёжность</h3><p data-v-358fc4a7>Стабильные сроки, понятные условия и ответственность за выполнение заказа</p></div><div class="value-card" data-v-358fc4a7><div class="value-icon" data-v-358fc4a7><svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor" data-v-358fc4a7><path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z" data-v-358fc4a7></path></svg></div><h3 data-v-358fc4a7>Забота о клиентах</h3><p data-v-358fc4a7>Подскажем, поможем выбрать и подстроимся под задачу — без бюрократии</p></div><div class="value-card" data-v-358fc4a7><div class="value-icon" data-v-358fc4a7><svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor" data-v-358fc4a7><path d="M12 3v10.55c-.59-.34-1.27-.55-2-.55-2.21 0-4 1.79-4 4s1.79 4 4 4 4-1.79 4-4V7h4V3h-6z" data-v-358fc4a7></path></svg></div><h3 data-v-358fc4a7>Развитие</h3><p data-v-358fc4a7>Расширяем ассортимент и услуги, внедряем технологии и улучшаем сервис</p></div></div></section><section class="stats-section" data-v-358fc4a7><h2 data-v-358fc4a7>Мы в цифрах</h2><div class="stats-grid" data-v-358fc4a7><div class="stat-item" data-v-358fc4a7><div class="stat-number" data-v-358fc4a7>15+</div><div class="stat-label" data-v-358fc4a7>лет на рынке</div></div><div class="stat-item" data-v-358fc4a7><div class="stat-number" data-v-358fc4a7>5000+</div><div class="stat-label" data-v-358fc4a7>клиентов</div></div><div class="stat-item" data-v-358fc4a7><div class="stat-number" data-v-358fc4a7>10000+</div><div class="stat-label" data-v-358fc4a7>заказов</div></div><div class="stat-item" data-v-358fc4a7><div class="stat-number" data-v-358fc4a7>100%</div><div class="stat-label" data-v-358fc4a7>контроль качества</div></div></div></section></div></div></main>',1)),t(d)]))}},p=v(f,[["__scopeId","data-v-358fc4a7"]]);export{p as default};
